const isAdjacentWithSpaceIgnored = (
    a: number[] | number,
    b: number,
    currentIndex: number,
    classTypes: number[]
): boolean => {
    const current = classTypes[currentIndex];
    if (Array.isArray(a) ? a.indexOf(current) !== -1 : a === current) {
        let i = currentIndex;
        while (i <= classTypes.length) {
            i++;
            let next = classTypes[i];

            if (next === b) {
                return true;
            }

            if (next !== SP) {
                break;
